class HomeFragment : Fragment() {
    private lateinit var homeViewModel: HomeViewModel
    private lateinit var rcy_bst_seller: RecyclerView
    private lateinit var rcy_top_rated: RecyclerView
    private lateinit var rcy_new_publised: RecyclerView
    private val lstBook_Best_seller=ArrayList<Best_Seller>();
    private val lstBook_Top_ratted=ArrayList<Top_rated>();
    private val lstBook_New_Published=ArrayList<New_Published>();
    override fun onCreateView(
            inflater: LayoutInflater,
            container: ViewGroup?,
            savedInstanceState: Bundle?
    ): View? {
        homeViewModel =
                ViewModelProvider(this).get(HomeViewModel::class.java)
        val root = inflater.inflate(R.layout.fragment_home, container, false)
//        val textView: TextView = root.findViewById(R.id.text_home)
//        homeViewModel.text.observe(viewLifecycleOwner, Observer {
//            textView.text = it
//        })
        return root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        rcy_bst_seller=view.findViewById(R.id.rcy_bst_seller)
        rcy_top_rated=view.findViewById(R.id.rcy_top_rated)
        rcy_new_publised=view.findViewById(R.id.rcy_new_publised)

        //----best selller boook apater---//
        book_Best_Seller()
        val adapter= activity?.let { best_seller_Adapter(lstBook_Best_seller, it) }
        val mLayoutManager = LinearLayoutManager(requireContext())
        mLayoutManager.orientation = LinearLayoutManager.HORIZONTAL
        rcy_bst_seller.layoutManager = mLayoutManager
        rcy_bst_seller.adapter=adapter;

        //----top rated books-----//
        topratted()
        val Topadapter= activity?.let { top_ratted_Adapter(lstBook_Top_ratted, it) }
        val LayoutManager = LinearLayoutManager(requireContext())
        LayoutManager.orientation = LinearLayoutManager.HORIZONTAL
        rcy_top_rated.layoutManager = LayoutManager
        rcy_top_rated.adapter=Topadapter;


        //----new published books----/
        NewPublished()
        val New_Published= activity?.let { new_Published_Adapter(lstBook_New_Published, it) }
        val NewLayoutManager = LinearLayoutManager(requireContext())
        NewLayoutManager.orientation = LinearLayoutManager.HORIZONTAL
        rcy_new_publised.layoutManager = NewLayoutManager
        rcy_new_publised.adapter=Topadapter;

    }
    private fun book_Best_Seller(){
        lstBook_Best_seller.add(Best_Seller("data:image/jpeg;base64,") )
        lstBook_Best_seller.add(Best_Seller("data:image/jpeg;base64,"))
        lstBook_Best_seller.add(Best_Seller("data:image/jpeg;base64,"))
        lstBook_Best_seller.add(Best_Seller("data:image/jpeg;base64,"))
        lstBook_Best_seller.add(Best_Seller("data:image/jpeg;base64,"))
        lstBook_Best_seller.add(Best_Seller("data:image/jpeg;base64,"))
        lstBook_Best_seller.add(Best_Seller("data:image/jpeg;base64,"))
    }
    private fun topratted(){
        lstBook_Top_ratted.add(Top_rated("data:image/jpeg;base64,"))
        lstBook_Top_ratted.add(Top_rated("data:image/jpeg;base64,"))
        lstBook_Top_ratted.add(Top_rated("data:image/jpeg;base64,") )
        lstBook_Top_ratted.add(Top_rated("data:image/jpeg;base64,"))
        lstBook_Top_ratted.add(Top_rated("data:image/jpeg;base64,"))
        lstBook_Top_ratted.add(Top_rated("data:image/jpeg;base64,"))
        lstBook_Top_ratted.add(Top_rated("data:image/jpeg;base64,"))
